document.addEventListener("DOMContentLoaded",(function(){let e=document.querySelector(".js-menu"),t=document.querySelector(".js-burger"),c=document.querySelectorAll(".js-menu-item");t.addEventListener("click",(function(){e.classList.contains("active")?e.classList.remove("active"):e.classList.add("active")})),c.forEach(t=>{t.addEventListener("click",(function(){e.classList.contains("active")||e.classList.add("active")}))}),document.addEventListener("click",t=>{t.composedPath().includes(e)||e.classList.contains("active")&&e.classList.remove("active")})}));